// Build events array for this lead
const lead = $input.first().json;
const events = [];

// Get email address (handle different column names)
const email = lead.email_address || lead['Email Adress '] || lead.Email || '';
if (!email) {
  return [{ json: { events: [], skipped: true, reason: 'No email address' } }];
}

// Campaign name
const campaign = 'Ohio';

// Generate random timestamp between Nov 20-28 (weekdays only)
// Campaign ran: Nov 20-28, 2025 (weekdays: 20,21,22,25,26,27,28)
const getRandomWeekdayTimestamp = () => {
  const weekdays = [
    '2025-11-20', // Wed
    '2025-11-21', // Thu
    '2025-11-22', // Fri
    '2025-11-25', // Mon
    '2025-11-26', // Tue
    '2025-11-27', // Wed
    '2025-11-28'  // Thu
  ];
  const randomDay = weekdays[Math.floor(Math.random() * weekdays.length)];
  // Random time between 9 AM - 5 PM
  const randomHour = 9 + Math.floor(Math.random() * 8);
  const randomMinute = Math.floor(Math.random() * 60);
  const randomSecond = Math.floor(Math.random() * 60);
  return new Date(`${randomDay}T${String(randomHour).padStart(2,'0')}:${String(randomMinute).padStart(2,'0')}:${String(randomSecond).padStart(2,'0')}.000Z`).toISOString();
};

// Check Email 1
if (lead.email_1_sent === 'Yes' || lead['Email #1 Sent'] === 'Yes') {
  events.push({
    contact_email: email,
    campaign: campaign,
    step: 1,
    event_type: 'sent',
    provider: 'gmail',
    provider_message_id: lead.message_id || lead['Message ID'] || `backfill-e1-${email}-${Date.now()}`,
    event_ts: getRandomWeekdayTimestamp(),
    subject: lead.email_1_subject || lead['Email #1 Subject'] || 'Email 1',
    body: lead.email_1_body || lead['Email #1 Body '] || ''
  });
}

// Check Email 2
if (lead.email_2_sent === 'Yes' || lead['Email #2 Sent'] === 'Yes') {
  events.push({
    contact_email: email,
    campaign: campaign,
    step: 2,
    event_type: 'sent',
    provider: 'gmail',
    provider_message_id: lead.message_id || lead['Message ID'] || `backfill-e2-${email}-${Date.now()}`,
    event_ts: getRandomWeekdayTimestamp(),
    subject: 'Re: ' + (lead.email_1_subject || lead['Email #1 Subject'] || 'Email 1'),
    body: lead.email_2_body || lead['Email #2 Body '] || ''
  });
}

// Check Email 3
if (lead.email_3_sent === 'Yes' || lead['Email #3 Sent'] === 'Yes') {
  events.push({
    contact_email: email,
    campaign: campaign,
    step: 3,
    event_type: 'sent',
    provider: 'gmail',
    provider_message_id: `backfill-e3-${email}-${Date.now()}`,
    event_ts: getRandomWeekdayTimestamp(),
    subject: lead.email_3_subject || lead['Email #3 Subject'] || 'Email 3',
    body: lead.email_3_body || lead['Email #3 Body'] || ''
  });
}

// Check Replied
if (lead.replied === 'Yes' || lead.Replied === 'Yes') {
  events.push({
    contact_email: email,
    campaign: campaign,
    event_type: 'replied',
    event_ts: getRandomWeekdayTimestamp()
  });
}

// Check Opted Out
if (lead.opted_out === 'Yes' || lead['Opted Out'] === 'Yes') {
  events.push({
    contact_email: email,
    campaign: campaign,
    event_type: 'opt_out',
    event_ts: getRandomWeekdayTimestamp()
  });
}

return [{ json: { events: events, lead_email: email, event_count: events.length } }];
